if (in_array(THIS_SCRIPT, array('showthread', 'showpost', 'editpost')))
{
    if (0 < (int) $this->registry->userinfo['userid'])
    {
        require_once(DIR . '/includes/class_votes.php');
        $vote_manager = Votes::get_instance(VOTE_CONTENT_TYPE, $post);

        // add votes result
        if (!$this->registry->GPC['ajax'])
        {
            $show['vote_result_hide'] = TRUE;
            if ('postbit' == $this->templatename OR 'postbit_legacy' == $this->templatename)
            {
                $show['vote_result_hide'] = FALSE;
            }
            $votes = $post['votes'];
            $vote_results = '';
            $vote_results .= $vote_manager->render_vote_result_bit(Votes::POSITIVE, $votes[Votes::POSITIVE]);
            if ($this->registry->options['vbv_enable_neg_votes'])
            {
                $vote_results .= $vote_manager->render_vote_result_bit(Votes::NEGATIVE, $votes[Votes::NEGATIVE]);
            }
            $template_hook['postbit_end'] .= $vote_results;
        }

        // add buttons
        $template_hook['postbit_buttons_end'] .= $vote_manager->render_vote_buttons();
    }
}

global $search_result_ids;
if (!empty($search_result_ids) AND is_array($search_result_ids))
{
    require_once(DIR . '/includes/class_votes.php');
    $searchbits = $this->template->unregister('searchbits');
    $vote_type = NULL;
    if (!$vbulletin->options['vbv_enable_neg_votes'])
    {
        $vote_type = Votes::POSITIVE;
    }

    $post_votes = array();
    if (can_see_results ())
    {
        $search_result_ids[] = '0';
        $post_votes = get_votes_for_post_list($search_result_ids, 'vBForum_Post', $vote_type);
        
    }

    $votes_result_search = $votes_result_replace = array();
    foreach ($search_result_ids as $post_id)
    {
        $votes_result_search[$post_id] = 'VOTES_RESULT_' . $post_id;
        $votes_result_replace[$post_id] = '';
        if (array_key_exists($post_id, $post_votes))
        {
            $votes = $post_votes[$post_id];
            if ($votes[Votes::POSITIVE])
            {
                $votes_count['positive_votes'] = count($votes[Votes::POSITIVE]);
            }
            if ($votes[Votes::NEGATIVE])
            {
                $votes_count['negative_votes'] = count($votes[Votes::NEGATIVE]);
            }
            $rcd_vbv_templater = vB_Template::create('vote_searchbit_stat');
            $rcd_vbv_templater->register('votes_count', $votes_count);
            $votes_result_replace[$post_id] = $rcd_vbv_templater->render();
        }
    }
    $searchbits = str_replace($votes_result_search, $votes_result_replace, $searchbits);
    $this->template->register('searchbits', $searchbits);
}

