// note: votes for old account will be remove into "Clear votes(user delete)" hook (userdata_delete) $sql = 'INSERT IGNORE INTO ' . TABLE_PREFIX . 'votes SELECT `targetid` , `contenttypeid` , `vote` , ' . $destinfo['userid'] . ' , `touserid` , `date` FROM ' . TABLE_PREFIX . 'votes WHERE `fromuserid` = '. $sourceinfo['userid']; $db->query_write($sql);
